package BasicProblem;

public class alphabet {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        char ch='a';
        while(ch <='z')
		 {
			if (ch=='a' || ch=='e' || ch=='i' || ch=='o' || ch=='u') 
			{
				System.err.println(ch +"is vowel");
			}
			else 
			{
				System.out.println(ch +"is console");
			}
			  ch++;
	     }
        
      

	}
	}
